#!/usr/bin/env python
"""A Nose plugin to support IPython doctests.
"""

from setuptools import setup, find_packages

setup(
    name='your_package_name',
    version='0.1',
    packages=find_packages(),
    extras_require={
        ':python_version < "3.7"': ['some_dependency'],
    },
    python_requires='>=3.6',
)